CREATE TABLE MyCourses (    CourseId INT PRIMARY KEY,    CourseName VARCHAR(100));CREATE TABLE My_Students (    StudentId INT PRIMARY KEY,    StudentName VARCHAR(50),    CourseId INT);CREATE TABLE Trainers (    TrainerId INT PRIMARY KEY,    TrainerName VARCHAR(50),    ManagerId INT);insert INTO MyCourses (CourseId, CourseName)VALUES(1, 'Database Management'),(2, 'Web Development'),(3, 'Data Science');insert INTO My_Students (StudentId, StudentName, CourseId)VALUES(1, 'Alice', 1),(2, 'Bob', 2),(3, 'Charlie', 1),(4, 'David', 3),(5, 'Eva', 2);insert INTO Trainers (TrainerId, TrainerName, ManagerId)VALUES(1, 'John Smith', NULL),(2, 'Jane Doe', 1),(3, 'Mike Johnson', 1),(4, 'Emily Davis', 2);--joins in sql--join is used to combine rows from two or more tables based on a related column between them--inner join returns only the rows that have matching values in both tablesSELECT s.StudentName, c.CourseNameFROM My_Students AS sINNER JOIN MyCourses AS c    ON s.CourseId = c.CourseId;--left join returns all rows from the left table and the matched rows from the right tableSELECT s.StudentName, c.CourseNameFROM My_Students AS sLEFT JOIN MyCourses AS c    ON s.CourseId = c.CourseId;--right join returns all rows from the right table and the matched rows from the left tableSELECT s.StudentName, c.CourseNameFROM My_Students AS sRIGHT JOIN MyCourses AS c    ON s.CourseId = c.CourseId;--full outer join returns all rows when there is a match in either left or right tableSELECT s.StudentName, c.CourseNameFROM My_Students AS sFULL OUTER JOIN MyCourses AS c    ON s.CourseId = c.CourseId;--self join is used to join a table with itselfSELECT t1.TrainerName AS Trainer, t2.TrainerName AS ManagerFROM Trainers AS t1LEFT JOIN Trainers AS t2    ON t1.ManagerId = t2.TrainerId;--cross join returns the Cartesian product of the two tablesSELECT s.StudentName, c.CourseNameFROM My_Students AS sCROSS JOIN MyCourses AS c;--union is used to combine the result sets of two or more SELECT statementssELECT StudentName FROM My_StudentsUNIONSELECT TrainerName FROM Trainers;